mapper.select:
select a.checker,a.product_id as productId,b.account_id as accountId, (case when c.child_account_name is not null then c.child_account_name else'离职员工' END) checkerName ,d.is_check as isCheck,b.product_name as productName, e.pic_path as picPath,f.first_up_time as firstUpTime,f.miles, b.* from carsok_dailycheck_dispatcher a left join carsok_acount g on g.id=a.account_id left join carsok_product b on a.product_id=b.id left join carsok_child_account c on a.checker=c.child_account_mobile and g.account=c.account_phone left join (select * from carsok_picture where type=1) e on a.product_id=e.product_id left join carsok_pruduct_oldcar f on a.product_id=f.pruduct_id left join (select * from carsok_dailycheck_result where DATE_FORMAT(check_date, '%Y-%m-%d')=#{checkDate}) as d on a.product_id=d.product_id where a.account_id=#{accountId} and DATE_FORMAT(a.create_time,'%Y-%m-%d')=#{checkDate}
and a.checker=#{checker}
order by a.checker

mapper.delete:
DELETE FROM carsok_dailycheck_dispatcher
WHERE account_id=#{accountId}
AND DATE_FORMAT(create_time, '%Y-%m-%d') = DATE_FORMAT(now(), '%Y-%m-%d')
